import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import './TypeThonHome.css';

function TypeThonHome() {
  const [typeThonList, setTypeThonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [scrollPosition, setScrollPosition] = useState(0);
  const containerRef = useRef(null);
  const scrollSpeed = 0.1; // 스크롤 속도 조절

  useEffect(() => {
    import('../../data/typeThon/typeThonList.json')
      .then((module) => {
        const data = module.default;
        console.log('Loaded data:', data);
        if (Array.isArray(data)) {
          setTypeThonList(data);
        } else {
          setError('Fetched data is not an array');
        }
      })
      .catch((error) => {
        setError('Error loading JSON data');
        console.error(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    const handleWheel = (event) => {
      event.preventDefault();
      setScrollPosition((prevPosition) => prevPosition + event.deltaY * scrollSpeed);
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    return () => {
      window.removeEventListener('wheel', handleWheel);
    };
  }, []);

  useEffect(() => {
    if (containerRef.current) {
      const anglePerItem = 360 / typeThonList.length;
      const radius = containerRef.current.offsetWidth / 2; // 원의 반지름
      const offset = -anglePerItem * Math.floor(scrollPosition / anglePerItem);

      typeThonList.forEach((item, index) => {
        const angle = anglePerItem * index + offset;
        const radians = (angle * Math.PI) / 180;
        const x = radius * Math.cos(radians);
        const y = radius * Math.sin(radians);

        const element = containerRef.current.children[index];
        element.style.transform = `translate(${x}px, ${y}px) rotate(${angle}deg) translate(-50%, -50%)`;

        // 디버깅용 콘솔 로그 추가
        console.log(`Element ${index}: x=${x}, y=${y}, angle=${angle}`);
      });
    }
  }, [typeThonList, scrollPosition]);

  const selectedItemIndex = Math.floor((scrollPosition % 360) / (360 / typeThonList.length));

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="type-thon-home">
      <div className="type-thon-container" ref={containerRef}>
        {typeThonList.map((topic, index) => (
          <Link
            key={index}
            to={`/type-thon/${topic.id}`}
            className={index === selectedItemIndex ? 'active' : 'inactive'}
          >
            {topic.title}
          </Link>
        ))}
      </div>
    </div>
  );
}

export default TypeThonHome;
