import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import './TypeThonHome.css';

function TypeThonHome() {
  const [typeThonList, setTypeThonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [scrollPosition, setScrollPosition] = useState(0);
  const containerRef = useRef(null);
  const requestRef = useRef(null);
  const scrollSpeed = 0.02; // 스크롤 속도 조절
  const dampingFactor = 0.9; // 감속 계수
  const itemSpacing = 100; // 텍스트 간의 간격 (픽셀)
  const circleRadius = 400; // 원의 반지름 (픽셀)
  const minScroll = 0; // 스크롤 최소 값
  const maxScroll = Math.max(0, itemSpacing * typeThonList.length - circleRadius * Math.PI * 2 * (240 / 360)); // 스크롤 최대 값 계산

  useEffect(() => {
    import('../../data/typeThon/typeThonList.json')
      .then((module) => {
        const data = module.default;
        console.log('Loaded data:', data);
        if (Array.isArray(data)) {
          setTypeThonList(data);
        } else {
          setError('Fetched data is not an array');
        }
      })
      .catch((error) => {
        setError('Error loading JSON data');
        console.error(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    const handleWheel = (event) => {
      event.preventDefault();
      setScrollPosition((prevPosition) => {
        const newPosition = prevPosition + event.deltaY * scrollSpeed;
        // 스크롤 범위를 minScroll에서 maxScroll로 제한하여 첫 텍스트에서 마지막 텍스트로만 스크롤 가능하도록 함
        return Math.min(Math.max(newPosition, minScroll), maxScroll);
      });
    };

    window.addEventListener('wheel', handleWheel, { passive: false });
    return () => {
      window.removeEventListener('wheel', handleWheel);
    };
  }, [typeThonList]);

  const animateScroll = () => {
    setScrollPosition((prevPosition) => {
      const newPosition = prevPosition * dampingFactor;
      if (Math.abs(newPosition) < 0.01) {
        cancelAnimationFrame(requestRef.current);
        return 0;
      }
      return newPosition;
    });

    requestRef.current = requestAnimationFrame(animateScroll);
  };

  useEffect(() => {
    if (containerRef.current) {
      const totalAngle = Math.min(240, typeThonList.length * (itemSpacing / circleRadius) * (180 / Math.PI)); // 240도 제한
      const anglePerItem = totalAngle / typeThonList.length;

      typeThonList.forEach((item, index) => {
        const angle = (index * anglePerItem - scrollPosition) % 360;
        const radians = (angle * Math.PI) / 180;
        const x = circleRadius * Math.cos(radians);
        const y = circleRadius * Math.sin(radians);

        const element = containerRef.current.children[index];
        element.style.transform = `translate(${x}px, ${y}px) rotate(${angle}deg) translateX(calc(-100% - 10px))`; // 텍스트 간격 조정

        // 선택된 텍스트가 우측에 위치하도록 설정
        if (Math.abs(angle % 360) < anglePerItem / 2) {
          element.classList.add('active');
          element.classList.remove('inactive');
        } else {
          element.classList.remove('active');
          element.classList.add('inactive');
        }

        // 디버깅용 콘솔 로그 추가
        console.log(`Element ${index}: x=${x}, y=${y}, angle=${angle}`);
      });
    }
  }, [typeThonList, scrollPosition]);

  useEffect(() => {
    requestRef.current = requestAnimationFrame(animateScroll);
    return () => cancelAnimationFrame(requestRef.current);
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="type-thon-home">
      <div className="type-thon-container" ref={containerRef}>
        {typeThonList.map((topic, index) => (
          <Link key={index} to={`/type-thon/${topic.id}`} className="inactive">
            {topic.title}
          </Link>
        ))}
      </div>
    </div>
  );
}

export default TypeThonHome;
